import { Injectable } from '@angular/core';
import { Observable, map, of } from 'rxjs';
import { MessageService } from './message.service';
import { HttpClient } from '@angular/common/http';
import { GET_HERO, GET_HEROES } from './graphql/graphql.queries';
import { Apollo } from 'apollo-angular';
import { Hero } from './heroes/hero';
import { HeroesGQL, HeroesQuery } from 'src/generated/graphql';

@Injectable({
  providedIn: 'root'
})
export class HeroService {

  heroes? : Hero[];
  hero? : Hero;

  constructor (private httpClient: HttpClient ,
    private messageservice: MessageService, 
    private apollo: Apollo,
    private heroesGql: HeroesGQL) { }

  getHeroes(): Observable<Hero[]> {                    
    const heroes = this.heroGql.watch().valueChanges.pipe(map(result => result.data.hero))
    return heroes;                                 
  }

  getHero(id: number): Observable<Hero> {
    const hero = this.apollo.query<Hero> ({
      query: GET_HERO,
      variables: {
        id: id,
      }
    }).pipe(map(response => response.data));
    console.log(hero);
    return hero;                                 
  }


}
