import {gql} from 'apollo-angular';

const GET_HEROES = gql`
    query {
        query heroes {
            heroes {
            name
            id
            }
       }
    }
    `

const GET_HERO = gql `
query hero($id: ID!) {
  hero(id: $) (
    id: 2
  )
  {
    name
    id
  }
}
`

export {GET_HEROES, GET_HERO};
