import {gql} from 'apollo-angular';

const GET_HEROES = gql`
query heroes {
  heroes {
  name
  id
}
}
`

const GET_HERO = gql `
query hero($id: Number!) {
  hero(
    id= $id
    )
  {
    name
    id
  }
}
`


export {GET_HEROES, GET_HERO};
