import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';
import { MessageService } from './message.service';
import { HttpClient } from '@angular/common/http';
import { GET_HERO, GET_HEROES } from './graphql/graphql.queries';
import { Apollo } from 'apollo-angular';
import { Hero } from './heroes/hero';
import { HeroGQL, HeroesQuery } from 'src/generated/graphql';

@Injectable({
  providedIn: 'root'
})
export class HeroService {

  heroes? : Observable<HeroesQuery['heroes']>;
  hero? : Hero;

  constructor (private httpClient: HttpClient ,
    private messageservice: MessageService, 
    heroesGql: H,
    private apollo: Apollo) { }

  getHeroes(): Observable<any> {                    
    const heroes = this.apollo.query<Hero[]> ({
      query: GET_HEROES,
    });
    return heroes;                                 
  }

  getHero(id: number): Observable<any> {
    const hero = this.apollo.query<Hero> ({
      query: GET_HERO,
      variables: {
        id: id,
      }
    });
    return hero;                                 
  }


}
