import { Component } from '@angular/core';
import { Hero } from './hero';
// import { HEROES } from '../mock-heroes'; no longer needed because we use it in service
import { HeroService } from '../hero.service';
import { MessageService } from '../message.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-heroes',
  templateUrl: './heroes.component.html',
  styleUrls: ['./heroes.component.css']
})
export class HeroesComponent {

  heroes: Observable<any> | undefined;      // we made this empty array because service will fill it auto
  selectedHero?: any;  

  constructor(private heroservice: HeroService, private messageService: MessageService) {} //injection

  ngOnInit(): void {     //this used to make getHeroes method excuted (lifecycle)
    this.getHeroes()
  }

  onSelected(hero: any): void{
    this.selectedHero = hero;
    this.messageService.add(`HeroesComponent Selected hero id=${hero.id}`);
  }



  getHeroes() {
    return this.heroservice.getHeroes().subscribe((data) => {
      this.heroes= data.data.heroes;
      console.log(this.heroes);
    })

  }
}
